package pattern;

public class pat_CN_Square {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=7;
        for(int i=1;i<=n;i++){
            for(int j=1;j<=n;j++){
                System.out.print(n);
            }
            System.out.println();
        }
	}

}
